INSERT INTO `%PREFIX%faq_qa` (`faqqa_id`, `faqqa_faq_id`, `faqqa_position`, `faqqa_time_stamp_mod`, `faqqa_status`, `faqqa_question`, `faqqa_answer`, `faqqa_auto_nl2br`) VALUES(1, 1, 1, '%TIMESTAMP%', 1, 'Welcome to phpcoin.', 'Congratulations on this installation of the phpCOIN (Clients, Order, Invoices, and Notes) package. Please be sure to perform the following:<br>\r\n<ul><li>Please review all documentation in the /coin_docs folder.\r\n<li>Please be sure set / verify the settings in the site config file.\r\n<li>Please be sure set / verify the settings in the theme config file.\r\n<li>Please be sure to backup your database after the initial setup is completed.\r\n</ul>\r\nPlease login to your admin control panel and set / verify required items.', 1);
