INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(1, 'email_password_reset', 'Hello $_MTP[username],\r\nThe following email from $_MTP[from_email] was triggered from a request to reset the password for this user name.\r\n\r\nThe new password is: $_MTP[password]\r\n\r\nPlease note the password has been reset. If you did not request the password change, we apologize for the inconvenience.\r\n\r\nAs a reminder, you can go to the following link to login and change you password.\r\n$_MTP[url]\r\n\r\n\r\n$_MTP[site] Support\r\n$_MTP[from_email]\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(2, 'email_contact_form', 'Hello $_MTP[to_name],\r\nThe following email from $_MTP[from_name] was sent through the $_MTP[site] contact form. Note- the sender has also been copied on this email.\r\n\r\nMessage as follows:\r\n-------------------\r\nTo: $_MTP[to_name]\r\nFrom: $_MTP[from_name]\r\nEmail: $_MTP[from_email]\r\n\r\nSubject: $_MTP[subject]\r\n\r\nMessage:\r\n$_MTP[message]\r\n\r\n-------------------\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(3, 'email_contact_client_form', 'Hello $_MTP[to_name],\r\nThe following email from $_MTP[from_name] was sent through the $_MTP[site] contact client form. Note- the sender may also be copied on this email.\r\n\r\nMessage as follows:\r\n-------------------\r\nTo: $_MTP[to_name]\r\nFrom: $_MTP[from_name]\r\nEmail: $_MTP[from_email]\r\n\r\nSubject: $_MTP[subject]\r\n\r\nMessage:\r\n$_MTP[message]\r\n\r\n-------------------\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(4, 'email_profile_copy', 'Hello $_MTP[to_name],\r\nThe following client profile copy is from $_MTP[site] $_MTP[from_name] department.\r\n\r\n-------------------\r\n$_MTP[cl_info]\r\n-------------------\r\n\r\nThis profile information can be edited online at the following link (requires login):\r\n $_MTP[cl_url]\r\n\r\nThank you for continuing to choose $_MTP[site] for your web service needs.\r\n\r\n $_MTP[from_name]\r\n $_MTP[from_email]\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(5, 'email_order_ack', 'Hello $_MTP[to_name],\r\nThe following order acknowledgement is from $_MTP[site] $_MTP[from_name] department.\r\n\r\n-------------------\r\n$_MTP[order]\r\n-------------------\r\n\r\nThis order can be monitored online at the following link (requires login):\r\n $_MTP[ord_url]\r\n\r\nThank you for continuing to choose $_MTP[site] for your web service needs.\r\n\r\n $_MTP[from_name]\r\n $_MTP[from_email]\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(6, 'email_order_copy', 'Hello $_MTP[to_name],\r\nThe following order copy is from $_MTP[site] $_MTP[from_name] department.\r\n\r\n-------------------\r\n$_MTP[order]\r\n-------------------\r\n\r\nThis order can be monitored online at the following link (requires login):\r\n $_MTP[ord_url]\r\n\r\nThank you for continuing to choose $_MTP[site] for your web service needs.\r\n\r\n $_MTP[from_name]\r\n $_MTP[from_email]\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(7, 'email_trans_ack', 'Hello $_MTP[to_name],\r\nThe following email transaction acknowledgement is from $_MTP[site] $_MTP[from_name] department.\r\n\r\n Invoice ID:    $_MTP[invc_id]\r\n Invoice Date:  $_MTP[invc_ts]\r\n Date Due:      $_MTP[invc_ts_due]\r\n Date Paid:     $_MTP[invc_ts_paid]\r\n Status:        $_MTP[invc_status]\r\n                ----------------------\r\n Trans ID:      $_MTP[it_id]\r\n Date:          $_MTP[it_ts]\r\n Type:          $_MTP[it_type]\r\n Origin:        $_MTP[it_origin]\r\n Description:   $_MTP[it_desc]\r\n Amount:        $_MTP[it_amount]\r\n                ----------------------\r\n\r\nThe following is overall account balance with $_MTP[site] and includes all account activity:\r\n\r\n Acc Balance:  $_MTP[cl_balance]\r\n\r\nThis transaction can be viewed when visiting following link (requires login):\r\n $_MTP[invc_url]\r\n\r\nThank you for continuing to choose $_MTP[site] for your web service needs.\r\n\r\n $_MTP[from_name]\r\n $_MTP[from_email]\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(8, 'email_invoice_copy', 'Hello $_MTP[to_name],\r\nThe following email invoice is from $_MTP[site] $_MTP[from_name] department.\r\n\r\n Invoice ID:    $_MTP[invc_id]\r\n Invoice Date:  $_MTP[invc_ts]\r\n Date Due:      $_MTP[invc_ts_due]\r\n Terms:         $_MTP[invc_terms]\r\n Date Paid:     $_MTP[invc_ts_paid]\r\n Billing Cycle: $_MTP[invc_bill_cycle]\r\n\r\n Subtotal Due:  $_MTP[invc_subtotal_cost]\r\n Tax-01:        $_MTP[invc_tax_01_amount] ($_MTP[invc_tax_01_percent]%)\r\n Tax-02:        $_MTP[invc_tax_02_amount] ($_MTP[invc_tax_02_percent]%)\r\n                ----------------------\r\n Total Due:     $_MTP[invc_total_cost]\r\n\r\nOur Tax Registration Number: $_MTP[Company_TaxNo]\r\n(see below for items included in this invoice)\r\n\r\nThis invoice can be paid by clicking on the Payment Link when visiting following link (requires login):\r\n $_MTP[invc_url]\r\n\r\nThank you for continuing to choose $_MTP[site] for your web service needs.\r\n\r\n $_MTP[from_name]\r\n $_MTP[from_email]\r\n $_MTP[Company_Name]\r\n\r\n\r\nThe following is a list of items included in this invoice:\r\n-------------------\r\n$_MTP[iitems]\r\n-------------------\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(9, 'email_invoice_copy_singleline', 'Hello $_MTP[to_name];\r\n\r\nThis email is an automated notification that a new invoice has been issued against your account.\r\nThis email summarizes the invoice, and the original can be viewed and/or printed by clicking on the following link (requires login):\r\n$_MTP[invc_url]\r\n\r\nYou may have a prior debit or credit balance, in which case the amount we expect to receive from you is$_MTP[cl_balance].  You may login to the hosting control panel to see how this amount was determined.\r\n\r\nThank you for continuing to choose $_MTP[Company_Name] for your web service needs.\r\n\r\nRespectfully Yours,\r\nBilling Department\r\n$_MTP[Company_Name]\r\n\r\nINVOICE DETAILS:\r\n================\r\nInvoice ID:     $_MTP[invc_id]\r\nInvoice Date:   $_MTP[invc_ts]\r\nDate Due:       $_MTP[invc_ts_due]\r\nTerms:          $_MTP[invc_terms]\r\nDate Paid:      $_MTP[invc_ts_paid]\r\nInvoice Amount: $_MTP[invc_total_cost]\r\nBilling Cycle:  $_MTP[invc_bill_cycle]\r\nNotes: $_MTP[invc_pay_link]\r\n\r\nLine   Item         Description                                      Cost\r\n-------------------------------------------------------------------------\r\n$_MTP[iitems]\r\n-------------------------------------------------------------------------\r\nSubtotal:  $_MTP[invc_subtotal_cost]\r\n$_CCFG[INVC_TAX_01_LABEL]       $_MTP[invc_tax_01_amount] ($_MTP[invc_tax_01_percent]%)\r\n$_CCFG[INVC_TAX_02_LABEL]       $_MTP[invc_tax_02_amount] ($_MTP[invc_tax_02_percent]%)\r\n=================================\r\nTotal:     $_MTP[invc_total_cost]\r\n\r\nOur Tax Registration: $_MTP[Company_TaxNo]');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(10, 'email_domain_acc_activate', 'Hello $_MTP[to_name],\r\n\r\nWelcome!  Thank you for choosing $_MTP[site] for your hosting needs.\r\n\r\nThis email contains vital information about your server account. Please read it thoroughly and print these pages or copy to a safe place.\r\n\r\n-------------------------------------------------------------\r\n1.	General Client Profile Information\r\n-------------------------------------------------------------\r\n\r\n$_MTP[cl_info]\r\n\r\n-------------------------------------------------------------\r\n2.	General Account Information\r\n-------------------------------------------------------------\r\n\r\nTo log into your account, please access:\r\n\r\n$_MTP[si_cp_url]\r\n or:\r\nhttps://$_MTP[si_ip]:$_MTP[si_cp_url_port]/login.php3\r\n\r\nServer Name:	$_MTP[si_name]\r\nIP Address:		$_MTP[si_ip]  (shared)\r\nDomain Name:	$_MTP[dom_domain]\r\nPOP Server:		mail.$_MTP[dom_domain]\r\nSMTP Server:	mail.$_MTP[dom_domain]\r\n\r\nNameservers:	$_MTP[si_ns_01]  (IP: $_MTP[si_ns_01_ip])\r\n				$_MTP[si_ns_02]  (IP: $_MTP[si_ns_02_ip])\r\n\r\nPlesk Username:	$_MTP[dom_user_name_cp]\r\nPlesk Password:	$_MTP[dom_user_pword_cp]\r\n\r\nFTP Username:	$_MTP[dom_user_name_ftp]\r\nFTP Password:	$_MTP[dom_user_pword_ftp]\r\nFTP URL:		ftp.$_MTP[dom_domain]  (once resolved)\r\n\r\nPlease remember that it will take a short time for your Registrar to process the delegation request and for this new DNS to propagate around the internet. During that time, you will not be able to access this site as normal through a web browser. However, you can still access your site during this period by pointing your browser to:\r\n\r\n		http://$_MTP[si_ip]/~$_MTP[dom_user_name_ftp]\r\n\r\nPlease note that CGI and PHP will not function properly while view your site through this special IP address. It is just a temporary measure to assist you in preparing your site while your new DNS transfer around the net.\r\n\r\nYou can upload your site by pointing your FTP software to your shared IP address ($_MTP[si_ip]). This means that you do not have to wait for your domain name to be transferred to our servers before you can upload your site. Once your domain has fully propagated around the net, you will be able to access your FTP account via your own domain name.\r\n\r\n\r\n-------------------------------------------------------------\r\n3.	Once your domain resolves:\r\n-------------------------------------------------------------\r\n\r\n	Your webmail URL is:	http://webmail.$_MTP[dom_domain]\r\n\r\n	Your Webstats URL is:	http://$_MTP[dom_domain]/webstat/\r\n	(stats can also be viewed from control panel)\r\n\r\n\r\n-------------------------------------------------------------\r\n4.	Setting Up An Email Account\r\n-------------------------------------------------------------\r\n\r\n	Before you can start sending or receiving email, you must\r\n	set up at least one email account.  To do this, please\r\n	follow the instructions below:\r\n\r\n	1. Log in to the control panel\r\n		($_MTP[si_cp_url])\r\n		using your control panel username and password.\r\n\r\n	2. Click on the MAIL button.\r\n\r\n	3. Enter the mail name that you want to create (i.e. "bob",\r\n		"editor", "webmaster" etc., not the full address).\r\n\r\n	4. Click on the ADD button\r\n\r\n	5. Check/Tick the "Mailbox" box\r\n\r\n	6. Enter and confirm a password that you would like to use\r\n\r\n	7. Scroll down to the bottom and click UPDATE\r\n\r\n\r\n-------------------------------------------------------------\r\n5.	Billing Information\r\n-------------------------------------------------------------\r\n\r\nFor increased security reasons we do not process credit cards ourselves on our servers. Depending on your selection of payment method when ordering, one of two third party processors will be used:\r\n\r\n	Paypal:			- Charge will appear as "Paypal"\r\n	Credit Card:	- Charge will appear as "2CheckOut.com Inc."\r\n\r\nRegardless of the method you selected, you will receive an order confirmation email detailing your order information.\r\n\r\nIf your email addresses changes, please notify us of your new email address and also update your profile in the control panel via the EDIT button.\r\n\r\n\r\n-------------------------------------------------------------\r\n6.	Support Methods\r\n-------------------------------------------------------------\r\n\r\nWhen looking for information or support. Please go through the motions:\r\n(see $_MTP[site] on the Client Tools menu for all these links)\r\n\r\na. FAQ Knowledgebase:\r\nFor general and other information, browse and search our FAQ:\r\n\r\n	http://www.yoursite.com/phpcoin/mod.php?mod=faq\r\n\r\n\r\nb. The $_MTP[site] Community:\r\nWe can	 stress enough, the importance of joining and participating in our community forums.  Often, you can find the answers to your questions just by searching for keywords or posting a question.  There are other members who can help you out with certain things that are not covered by our normal support options (Programming etc). You can also interact with $_MTP[site] staff, give us feedback and generally discuss our service at a community level.\r\n\r\n	http://forums.yoursite.com\r\n\r\nImportant note: The community is installed on a different server than the primary website. The reason for this is in the event you cannot reach the main site due to server issues, you should be able to reach the community for information and updates.\r\n\r\n\r\nc. HelpDesk:\r\nIf you require assistance please contact our Technical Support Team by submitting a support form. We will be happy to assist you. All support requests MUST be done using our ticket support system, which can be accessed at:\r\n\r\n	http://www.yoursite.com/phpcoin/mod.php?mod=helpdesk\r\n\r\n\r\nd. Quick Chat:\r\nFor immediate assistance, check the Quick Chat feature setup on the main site. Simply click on the "Quick Chat" link under the client Tools menu. Login and and quite possibly someone will be there who can assist you.\r\n\r\n\r\ne. Email Support:\r\nThis form of support is mostly used for sales, orders, or basic questions. Please view the Contacts Info link on the main site for up-to-date links for email options.\r\n\r\n\r\nf. Phone Support:\r\nCurrently, $_MTP[site] does not offer phone support.\r\n\r\n\r\nh. Regular Mail:\r\n\r\n	$_UVAR[CO_INFO_01_NAME]\r\n	c/o Customer Service\r\n	$_UVAR[CO_INFO_02_ADDR_01]\r\n	$_UVAR[CO_INFO_03_ADDR_02]\r\n	$_UVAR[CO_INFO_04_CITY], $_UVAR[CO_INFO_05_STATE_PROV]	$_UVAR[CO_INFO_06_POSTAL_CODE]\r\n	$_UVAR[CO_INFO_07_COUNTRY]\r\n\r\n	Phone:	$_UVAR[CO_INFO_08_PHONE]\r\n	Fax:	$_UVAR[CO_INFO_09_FAX]\r\n\r\nOnce again, this email contains vital information about your account. \r\nPlease read it thoroughly and print these pages or copy to a safe place.\r\n\r\nThank you again for choosing $_MTP[site] as your service provider.\r\n\r\n $_MTP[from_name]\r\n $_MTP[from_email]\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(11, 'email_helpdesk_tt_update', 'Hello $_MTP[to_name],\r\nThe following email HelpDesk Ticket is from $_MTP[site] $_MTP[from_name] department and is the result of $_MTP[req_type].\r\n\r\nThis ticket can be viewed by clicking on the following link (requires login):\r\n $_MTP[tt_url]\r\n\r\nThank you for continuing to choose $_MTP[site] for your web service needs.\r\n\r\n $_MTP[from_name]\r\n $_MTP[from_email]\r\n\r\n------------------------------\r\n Ticket ID:    $_MTP[hd_tt_id]\r\n Date Opened:  $_MTP[hd_tt_time_stamp]\r\n Priority:     $_MTP[hd_tt_priority]\r\n Category:     $_MTP[hd_tt_category]\r\n Domain:       $_MTP[cd_cl_domain]\r\n\r\n Status:       $_MTP[hd_tt_status]\r\n Open/Closed:  $_MTP[hd_tt_closed]\r\n Rating:       $_MTP[hd_tt_rating]\r\n\r\n Subject:      $_MTP[hd_tt_subject]\r\n Message (below):\r\n ----------------\r\n$_MTP[hd_tt_message]\r\n------------------------------\r\n\r\nThe following is a listing of HelpDesk Support Ticket messages to date:\r\n\r\n$_MTP[tt_msgs]\r\n------------------------------\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(12, 'email_helpdesk_tt_alert', ' Ticket ID:    $_MTP[hd_tt_id]\r\n Priority:     $_MTP[hd_tt_priority]\r\n Subject:      $_MTP[hd_tt_subject]\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(13, 'email_custom_order_request', 'Hello $_MTP[to_name],\r\nThe following email from $_MTP[from_name] was sent through the $_MTP[site] Custom Order Request system, and is a copy for your records.\r\n\r\nWe will be contacting you with the results of our processing the information you requested.\r\n\r\nThe following client profile was created as a result of your order request. In the event you choose to place the order, you can login to place the order.\r\n\r\n-------------------\r\n$_MTP[cl_info]\r\n-------------------\r\n\r\nThe following is the information you entered into the Custom Order Request form.\r\n\r\n-------------------\r\n$_MTP[cor_info]\r\n-------------------\r\n\r\n\r\nThank you for continuing to choose $_MTP[site] for your web service needs.\r\n\r\n $_MTP[from_name]\r\n $_MTP[from_email]\r\n');
INSERT INTO `%PREFIX%mail_templates` (`mt_id`, `mt_name`, `mt_text`) VALUES(14, 'email_contact_supplier_form', 'Hello $_MTP[to_name],\r\nThe following email from $_MTP[from_name] was sent through the $_MTP[site] contact supplier form.\r\n\r\nMessage as follows:\r\n-------------------\r\nTo: $_MTP[to_name]\r\nFrom: $_MTP[from_name]\r\nEmail: $_MTP[from_email]\r\n\r\nSubject: $_MTP[subject]\r\n\r\nMessage:\r\n$_MTP[message]\r\n\r\n-------------------\r\n');
