INSERT INTO `%PREFIX%pages` (`id`, `subject`, `topic_id`, `cat_id`, `time_stamp`, `pages_title`, `pages_code`, `pages_block_it`, `pages_status`, `pages_admin`, `pages_link_menu`, `pages_link_prev`, `pages_link_home`, `pages_link_next`) VALUES(1, 'Backup Failed', 3, 1, '%TIMESTAMP%', 'Backup Failed', 'The MySQL database backup procedure has completed, but for some reason the file was not written to the server. Please check the location in Admin -> parameters -> subgroup=backup and ewnsure the the webserver user has write permission to that location', 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `%PREFIX%pages` (`id`, `subject`, `topic_id`, `cat_id`, `time_stamp`, `pages_title`, `pages_code`, `pages_block_it`, `pages_status`, `pages_admin`, `pages_link_menu`, `pages_link_prev`, `pages_link_home`, `pages_link_next`) VALUES(2, 'Backup Disallowed', 3, 1, '%TIMESTAMP%', 'Backup Disallowed', 'Your attempt to backup the database has been disallowed, sorry. Either you are not an admin, or you used a POST/GET request from outside this website.', 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `%PREFIX%pages` (`id`, `subject`, `topic_id`, `cat_id`, `time_stamp`, `pages_title`, `pages_code`, `pages_block_it`, `pages_status`, `pages_admin`, `pages_link_menu`, `pages_link_prev`, `pages_link_home`, `pages_link_next`) VALUES(3, 'Backup Completed', 3, 1, '%TIMESTAMP%', 'Backup Completed', 'The MySQL database backup procedure has completed. Depending on the backup option you chose, you will now have one of the following:<ul>\r\n<li>A MySQL dump file of the database on your computer, or</li>\r\n<li>A MySQL dump of the database in a directory on your webserver, or</li>\r\n<li>An email message with a MySQL dump of the database attached.</li>\r\n</ul>', 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'B', 'HELPDESK_AUTO_IMPORT_ENABLE', 'Auto-import: Enable', '1', 'YES: The helpdesk cronjob will connect to a POP or IMAP box and attempt to create helpdesk tickets or ticket messages from any emails found\r\nNO: (obvious)');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'S', 'HELPDESK_AUTO_IMPORT_SERVER', 'Auto-import: Server', 'mail.%DOMAINNAME%', 'Name of the email server the helpdesk cronjob should connect to');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'S', 'HELPDESK_AUTO_IMPORT_TYPE', 'Auto-import: Protocol', ':143/imap/notls', 'Protocol and port that the helpdesk cronjob should connect to\r\n"/imap:143" seems to work better than "/pop:110"\r\nYou may also need to try /IMAP/:143/notls or /:110/POP/notls or even other strings, depending on your mailserver');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'S', 'HELPDESK_AUTO_IMPORT_USERID', 'Auto-import: UserName', 'support@%DOMAINNAME%', 'The email username that the helpdesk cronjob will check');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'S', 'HELPDESK_AUTO_IMPORT_PASSWORD', 'Auto-import: Password', 'password', 'Password for the email account that will be checked by the helpdesk cronjob');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'I', 'HELPDESK_AUTO_IMPORT_DELETE_MESSAGES', 'Auto-import: Delete Messages', '1', 'Delete _these_ messages on the mail-server when the helpdesk cronjob runs');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'S', 'HELPDESK_AUTO_IMPORT_DEFAULT_CATEGORY', 'Auto-import: Category', 'General', 'Default "category" for helpdesk tickets auto-created by the helpdesk cronjob');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'S', 'HELPDESK_AUTO_IMPORT_DEFAULT_PRIORITY', 'Auto-import: Priority', 'Medium Priority', 'Default "priority" for helpdesk tickets auto-created by the helpdesk cronjob');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'S', 'HELPDESK_AUTO_IMPORT_DEFAULT_STATUS', 'Auto-import: Status', 'Awaiting Support', 'Default "status" for helpdesk tickets auto-created by the helpdesk cronjob');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'invoices', 'B', 'INVC_ACOPY_DELAY_ENABLE', 'Invoice Auto-Copy: Delay', '1', 'YES: Wait until new invoice date is within days-out setting before auto-copying new invoice. Auto-invoices will not sit as "pending" for a long time, and invoice numbers will be close to sequential\r\nNO: Auto-copy new invoice as soon as possible. An auto-invoice for next year will sit as "pending" for almost a year, and invoice numbers will not be sequential');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'invoices', 'I', 'INVC_ACOPY_DAYS_OUT', 'Invoice Auto-Copy: Days Out to Copy', '14', 'The number of days from current date to auto-copy a recurring invoice. Auto-copy will occur if the new invoice date falls within this date range');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'invoices', 'B', 'INVC_AUTO_UPDATE_ENABLE', 'Invoice Auto-Copy: Auto-Update Status', '1', 'YES: Auto-update of invoice status will occur when triggered by cron job or when run manually by command summary actions menu\r\nNO: Auto-update of invoice status will not occur');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'invoices', 'B', 'INVC_AUTO_COPY_ENABLE', 'Invoice Auto-Copy: Enable', '1', 'YES: Auto-copy of invoices will occur when triggered by cron job or when run manually by command summary actions menu\r\nNO: Auto-copy of invoices will not occur');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'invoices', 'B', 'INVC_AUTO_REMINDERS_ENABLE', 'Invoice Reminders: Autosend Overdue Reminders', '1', 'YES: phpCOIN will auto-send the specified email message on the specified overdue day(s)\r\nNO: You must manually remind your customers that they are overdue.\r\nYou may setup as many event-dates and templates as you need.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'invoices', 'B', 'INVC_SOON_REMINDERS_ENABLE', 'Invoice Reminders: Autosend Soon-Due Reminders', '1', 'YES: phpCOIN will auto-send "reminders" email message number zero half way between the invoice issued and due dates\r\nNO: There will be NO reminders sent that an invoice will soon be due');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'cronjobs', 'helpdesk', 'B', 'HELPDESK_AUTO_VERBOSE', 'Auto-import: Verbose', '0', 'YES: phpCOIN will display the results of processing of each email message\r\nNO: phpCOIN will only display aggregate results');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'invoices', 'B', 'ORDER_AUTO_CREATE_INVOICE', 'Order Invoice: Auto-Create From Order', '1', 'YES: Auto-create an invoice with the default terms, due dates, etc. every time a product is ordered\r\nNO: No invoice will be auto-created');
