INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'invoices', 'S', 'AUTO_INV_DELIVERY', 'Order Invoice: Default Delivery Method', 'email', 'Default delivery method for invoices auto-created by the order module.');
