INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'admin', 'B', 'AUTOCHECK_UPDATES', 'Automatically Check For Updates: Enable', '1', 'YES: phpCOIN will check for new versions and bug-fixes each time you click the [Admin] or [Summary] button.  This enables phpCOIN to advise you when a new version or bug-fix is available, and provides a direct link to the actual download.  This feature uses an RSS feed, and does NOT send any information whatsoever to phpCOIN.com\r\nNO: Do not check for available updates and bug-fixes');
