INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'domains', 'S', 'DOM_DEFAULT_USERNAME', 'CP/FTP: Default User Name', 'username', 'The default CP/FTP User Name inserted when a domain account is created by the order process. Choose "username" to insert client user name, or "domain" to insert the domain name');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'domains', 'S', 'DOM_DEFAULT_CP_URL', 'Default: Control Panel URL', '', 'The URL (fully qualified) that will be used for the control panel link shown in domain listings. Variables that can be used her and will be replaced with actual values at run-time are:\r\n%DOM_ID% the domain_id\r\n%DOM_NAME% the domain name\r\n%CL_ID% client_id\r\n%FTP_NAME% default CP/FTP username\r\n%FTP_PWORD% default CP/FTP password');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'domains', 'B', 'DOM_CP_URL_LINK_ENABLE', 'List: Enable CP Link', '1', 'YES: The control panel link will be shown in domain listings\r\nNO: The CP link will not be shown in listings');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'domains', 'B', 'MATCH_CP_LOGIN_TO_ACCOUNT', 'CP/FTP: Use phpCOIN Password For FTP', '0', 'NO: A random password for FTP will be generated by phpCOIN.\r\nYES: The FTP password created by phpCOIN will match the username and password entered by the client for accessing phpCOIN. This means that you will know the u/p for account creation with cPanel, etc., but it is a security risk because this info is stored in the clear in the database');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'domains', 'B', 'SHOW_PASSWORDS_TO_CLIENTS', 'CP/FTP: Show Passwords To Clients', '1', 'YES: Clients can see their plaintext passwords for FTP and Control Panel access when viewing domain info\r\nNO: Only admins can see the passwords');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'domains', 'B', 'DOMAINS_ENABLE', 'Domains: Enable', '1', 'YES: Module is enabled and ready for use\r\nNO: Module is deactivated, all menu buttons/links are automatically hidden, and no output from this module will be visible');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'helpdesk', 'I', 'IPP_HELPDESK', 'List: Items Per Page', '25', 'The number of helpdesk tickets that are displayed  in listing form on a page');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'helpdesk', 'I', 'BASE_HELPDESK_ID', 'HelpDesk TT ID Base-Initial Value Minus 1', '1000', 'Determines the helpdesk ticket id initial value for the first helpdesk ticket entered in the system. Helpdesk Ticket ID will be this value plus one.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'helpdesk', 'B', 'HELPDESK_ENABLE', 'HelpDesk: Enable', '1', 'YES: Module is enabled and ready for use\r\nNO: Module is deactivated, all menu buttons/links are automatically hidden, and no output from this module will be visible');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'helpdesk', 'S', 'HELPDESK_ALERT_EMAIL_ADDRESS', 'HelpDesk TT Alert Email: Address', 'webmaster@%DOMAINNAME%', 'The email (pager email) address the Alert Email is sent to. Does not effect normal support email received');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'helpdesk', 'B', 'HELPDESK_ALERT_EMAIL_ENABLE', 'Helpdesk TT Alert Email: Enable', '1', 'YES: An Alert Email is also sent when a new ticket is created, in addition to normal email to support\r\nNO: Only the normal email to support is sent');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'helpdesk', 'B', 'HELPDESK_ADMIN_REVEAL_ENABLE', 'HelpDesk Admin: Reveal Admin Identity', '1', 'YES: The specific admin responding to HelpDesk items will be identified in listings and emails\r\nNO: HelpDesk listings and emails will show as being from "support"');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'helpdesk', 'B', 'HELPDESK_MSG_CC_CLIENT_ENABLE', 'Helpdesk Message Email: CC To Client', '0', 'YES: Client will receive a CC of the email when they submit a helpdesk message\r\nNO: Only support receives the message');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'helpdesk', 'B', 'HELPDESK_REPLY_EMAIL_SET_LIMIT', 'HelpDesk Reply: Limit Messages Sent', '1', 'YES: Include only the X most recent messages for the HelpDesk Ticket in the reply notice email\r\nNO: Include all messages for the HelpDesk Ticket in the reply notice email');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'helpdesk', 'I', 'HELPDESK_REPLY_EMAIL_LIMIT', 'Helpdesk Reply: Email Messages Limit', '2', 'The number of most recent messages to include in the reply notice email.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'helpdesk', 'B', 'HELPDESK_SHOW_CLIENT_NAME', 'List: Show Client Name', '1', 'Enable the Client column in the helpdesk tickets listing when viewed by admins');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'helpdesk', 'B', 'HELPDESK_ADMIN_CAN_ADD', 'HelpDesk Admin: Admin Can Enter Tickets', '1', 'YES: An administrator can enter a new support ticket on behalf of a client\r\nNO: Only a logged-in client can enter a new support ticket.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'I', 'IPP_INVOICES', 'List: Items Per Page', '25', 'Determines the number of invoices that are displayed  in listing form on a page.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'invoices', 'I', 'BASE_INVOICE_ID', 'Invoice ID Initial Value', '1000', 'Starting point for the first invoice entered in the system. Invoice ID will be this value plus one');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'invoices', 'B', 'INVC_AUTO_EMAIL_ENABLE', 'Invoice Auto-Email to Clients Enable', '1', 'YES: Auto-email of due invoices is enabled when triggered by cron job or manually by command summary actions menu');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'invoices', 'B', 'INVC_AUTO_EMAIL_CC_ENABLE', 'Invoice Auto-Email CC Admin Enable', '0', 'YES: Admin will receive a copy each time the auto-email code sends an invoice');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'invoices', 'S', 'INVC_DEL_MTHD_DEFAULT', 'Invoice Delivery Method Default Value', 'email', 'The default invoice delivery method presented on invoice create');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'invoices', 'I', 'INVC_DUE_DAYS_OFFSET', 'Invoice Due x Days After Create', '10', 'When an invoice is created, phpCOIN will add this many days to the create date in order to auto-calc the due date');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'invoices', 'B', 'INVC_EMAIL_CC_ENABLE', 'Invoice Email CC Admin Enable', '0', 'YES: Admin will receive a copy each time a request is made to email an invoice');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'invoices', 'B', 'INVC_TAX_01_ENABLE', 'Invoices: Tax 01 Enable', '1', 'YES: Tax 1 will be calculated and displayed on invoices\r\nNO: Tax 1 will NOT be calculated and displayed on invoices');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'invoices', 'B', 'INVC_TAX_02_ENABLE', 'Invoices: Tax 02 Enable', '1', 'YES: Tax 2 will be calculated and displayed on invoices\r\nNO: Tax 2 will NOT be calculated and displayed on invoices');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'B', 'INVC_TERMS_INSERT_DEF', 'Invoice Terms: Auto-Insert Default', '1', 'YES: When terms are enabled, the terms field is auto-populated with the default terms on new invoices if admin left them empty on save\r\nNO: When terms are enabled, the terms field is left blank on new invoices if admin left them empty on save');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'B', 'INVC_TERMS_ENABLE', 'Invoice Terms: Enable', '1', 'YES: The invoice terms field is displayed on invoice views');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'B', 'INVC_SHOW_CLIENT_PENDING', 'Invoices: Show Pending To Client', '0', 'YES: The client will see invoices that are set to pending status. Useful as advance warning about upcoming invoices\r\nNO: The client will not see invoices that are set to pending status. No advance warning about upcoming invoices');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'B', 'INVC_SPLIT_TRANS_LIST_ENABLE', 'Invoice Transaction Listing: Enable Split Listing', '0', 'YES: The invoice transaction listing is split to list charges on top, and then credits below in a separate list\r\nNO: invoice transactions appear in a single section one after the other, like a standard statement');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'invoices', 'B', 'INVC_TAX_BY_ITEM', 'Invoice Taxes: Tax By Item', '1', 'YES: Taxes will be calculated on each item individually\r\nNO: Taxes will be calculated only on the invoice totals');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'S', 'INVC_TAX_01_LABEL', 'Invoice Tax 01: Label', '13% HST:', 'The text label displayed on bills in place of "Tax 01"');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'invoices', 'R', 'INVC_TAX_01_DEF_VAL', 'Invoice Tax 01: Rate', '13.00', 'The default tax rate percentage value used for this tax during auto-calc. For example: 1.00 is 1 percent');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'S', 'INVC_TAX_02_LABEL', 'Invoice Tax 02: Label', '5% PST:', 'The text label displayed on bills in place of "Tax 02"');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'invoices', 'R', 'INVC_TAX_02_DEF_VAL', 'Invoice Tax 02: Rate', '5.00', 'The default tax rate percentage value used for this tax during auto-calc. For example: 1.00 is 1 percent');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'I', 'INVC_VIEW_SHOW_TRANS', 'Invoice Transaction Listing: Show Transactions', '3', 'If the transactions listing is displayed on lower portion of invoice:\r\n0: No transactions displayed\r\n1: Transactions displayed on-screen only\r\n2: Transactions displayed on printed output only\r\n3: Transactions appear on both screen and printed output');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'B', 'PRICES_INCLUDE_TAXES', 'Invoice Taxes: Prices Include Taxes', '0', 'NO: phpCOIN will add applicable taxes to items on invoices\r\nYES: phpCOIN assumes that the prices include tax. In this event, taxes will be removed from the items and then recalculated, so the invoice can show the amount of taxes included in the prices.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'B', 'SINGLE_LINE_EMAIL_INVOICE_ITEMS', 'Invoice Email: Single Line Per Item', '1', 'YES: Set each item on an emailed invoice to be single-line like a paper invoice. The formatting only works for text invoices and will not line up properly if outgoing html emails are enabled\r\nNO: Use multiple lines to describe each item. Layout is not messed up with outgoing html emails enabled, but it does not look like and invoice');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'invoices', 'B', 'INVOICES_ENABLE', 'Invoices: Enable', '1', 'YES: Module is enabled and ready for use\r\nNO: Module is deactivated, all menu buttons/links are automatically hidden, and no output from this module will be visible');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'invoices', 'B', 'INVC_SHOW_LAST_NAG', 'Invoices: Show Last Nag', '1', 'YES: An additional info-box containing the name of the last nag email sent (if any) will be displayed as part of the invoice');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_PKG_FOOTER_LINE_01', 'Site Footer Block: Line 01 Text', 'Copyright %DOMAINNAME% 2003-2009, all rights reserved', 'The line 01 text that will be displayed in footer of the page output');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_PKG_FOOTER_LINE_02', 'Site Footer Block: Line 02 Text', '', 'The line 02 text that will be displayed in footer of the page output');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'email', 'B', '_PKG_EMAIL_OUT_ENABLE', 'Email: Enable', '1', 'YES: phpCOIN will send all normal emails\r\nNO: The main email functions are disabled from sending email');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'email', 'B', 'MAIL_USE_CUSTOM_SUBJECT', 'Email Contact Client: Use Custom Subject', '1', 'YES: Use the subject that you entered on the form instead\r\nNO: Use the default subject line of "sitename- Client Contact Message" on emails sent using the [eMail Client] form');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'email', 'B', '_PKG_SAFE_EMAIL_ADDRESS', 'Email: Use Safe Address Format', '0', 'YES: Emails will be sent as only <name@address> which every mail-server will accept\r\nNO: Emails sent from phpCOIN use the "Name <name@address>" format. Some mail-servers have a problem with this');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'email', 'B', 'EMAIL_AS_HTML', 'HTML Email: Enable HTML Outgoing', '1', 'YES: phpCOIN will send html emails\r\nNO: phpCOIN will send plain-text only emails');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'admin', 'B', 'ENABLE_QUICK_EDIT', 'Quick-Edit Icons: Enable', '1', 'YES: Display quick-access icons where appropriate for editing parameters and page text, rather than forcing admin to goto Admin and select menu items\r\nNO: Settings and content can only be edited by clicking through the relevant buttons and menus');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'I', 'IPP_ORDERS', 'List: Items Per Page', '25', 'The number of orders that are displayed in listing form on a page');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'orders', 'I', 'BASE_ORDER_ID', 'Order ID Base-Initial Value Minus 1', '1000', 'Determines the order id initial value for the first order entered in the system. Order ID will be this value plus one.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'orders', 'B', 'ENABLE_EMAIL_ORDER_OUT', 'Order Email: Mail to Admin on Order Out', '1', 'YES: An email is sent to admin when client is showed the order paylink (order inserted into system)');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'orders', 'B', 'ENABLE_EMAIL_ORDER_RET', 'Order Email: Mail to Admin on Order Return', '1', 'YES: An email is sent to admin when client is returned to phpCOIN from billing vendor');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'B', 'ORDER_POLICY_BTTN_AUP', 'Order Policy Button Show: AUP', '1', 'Determines if order policy button for Acceptable Use Policy is shown on place order screens.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'B', 'ORDER_POLICY_BTTN_BC', 'Order Policy Button Show: BC', '1', 'Determines if order policy button for Banned Code is shown on place order screens.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'B', 'ORDER_POLICY_BTTN_PP', 'Order Policy Button Show: PP', '1', 'Determines if order policy button for Privacy Policy is shown on place order screens.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'B', 'ORDER_POLICY_BTTN_TOS', 'Order Policy Button Show: TOS', '1', 'Determines if order policy button for Terms Of Service is shown on place order screens.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'orders', 'I', 'ORDER_POLICY_SI_ID_AUP', 'Order Policy SiteInfo ID for: AUP', '6', 'Determines the siteinfo page id for the Acceptable Use Policy button link.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'orders', 'I', 'ORDER_POLICY_SI_ID_BC', 'Order Policy SiteInfo ID for: BC', '8', 'Determines the siteinfo page id for the Banned Code Policy button link.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'orders', 'I', 'ORDER_POLICY_SI_ID_PP', 'Order Policy SiteInfo ID for: PP', '5', 'Determines the siteinfo page id for the Privacy Policy button link.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'orders', 'I', 'ORDER_POLICY_SI_ID_TOS', 'Order Policy SiteInfo ID for: TOS', '7', 'Determines the siteinfo page id for the Terms Of Service button link.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'orders', 'B', 'ORDER_EMAIL_CC_ENABLE', 'Order Email: CC To Admin', '1', 'YES: The site admin will receive a copy each time a request is made to email a order');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'orders', 'B', 'ORDERS_ACK_EMAIL_ENABLE', 'Order Email: Ack Email To Client', '1', 'YES: An acknowledgment email will be sent to client on new order');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'orders', 'B', 'ORDERS_ACK_EMAIL_ONRET', 'Order Email: Ack Email To Client On Return', '1', 'An acknowledgment email will be sent to client on return from vendor, instead of upon paylink display');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'orders', 'B', 'ORDERS_ENABLE', 'Orders: Enable', '1', 'YES: Module is enabled and ready for use\r\nNO: Module is deactivated, all menu buttons/links are automatically hidden, and no output from this module will be visible');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'orders', 'B', 'ORDERS_AUP_ENABLE', 'Orders Req. Acceptable Use Policy (AUP): Enable', '1', 'YES: The "Acceptable Use Policy" button is visible and client must check "I agree to AUP" box during order placement');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'orders', 'B', 'ORDERS_COR_ENABLE', 'Custom Order Request (COR): Enable', '1', 'YES: a surfer can click the COR button to submit a price request for a custom order during the order process\r\nNO: The surfer can order only pre-defined products and services');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'orders', 'B', 'ORDERS_TOS_ENABLE', 'Orders Req. Terms Of Service (TOS): Enable', '1', 'YES: The "Terms of Service" button is visible and client must check "I agree to TOS" box during order placement');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'orders', 'S', 'ORDERS_DEF_STATUS_NEW', 'Orders: Default Status on Create', 'pending', 'The default order status used when an order is placed');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'I', 'ORDERS_FIELD_ENABLE_COR', 'Order Form: Enable Fields On COR', '65504', 'Determines which fields are enabled (visible) on the orders COR form.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'I', 'ORDERS_FIELD_ENABLE_ORD', 'Order Form: Enable Fields On Form', '65504', 'Determines which fields are enabled (visible) on the orders order form. This also effects client editing forms.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'I', 'ORDERS_FIELD_REQUIRE_COR', 'Order Form: Require Fields On COR', '49184', 'Determines which fields are required (visible) on the orders COR form.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'I', 'ORDERS_FIELD_REQUIRE_ORD', 'Order Form: Require Fields On Form', '24448', 'Determines which fields are required (visible) on the orders order form. This also effects client editing forms.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'B', 'ORDERS_LIST_SHOW_PROD_DESC', 'Order Form: Show Product Description', '1', 'Determines if the Product description will be displayed on Orders Listings instead of the default Vendor / Product Name columns.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'I', 'ORDERS_PROD_DISPLAY_SEQUENCE', 'Order Form: Products Display Sequence', '1', 'What to display on the Products List shown to clients when ordering.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'I', 'ORDERS_PROD_LIST_SORT_ORDER', 'Order Form: Product List Primary Sort Field', '1', 'Determines which field will be the ascending sort order for the order screen products list.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'orders', 'I', 'DEFAULT_PAYMENT_METHOD', 'Default Payment Method On Order Form', '0', 'The billing method to use on a new order, or let client select payment method from a drop-down list of vendors. If you specify a vendor here, all modules that normally display the billing vendor will no longer do so.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'orders', 'R', 'ORDER_SETUP_FEE', 'Orders: Add Order Setup Fee', '0', 'Any amount here other than zero will be added to the price of the product ordered as a setup fee, and the words " (plus setup fee)" will be appended to the item description on the order');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'I', 'ORDERS_PROD_LIST_SIZE', 'Order Form: Product List Size', '1', '0: Products will be displayed as a radio list on the order form\r\nAny non-zero value: phpCOIN will use a select-list with that many rows visible');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'B', 'ORDERS_ITEMS_SUB-LIST', 'Orders: Group Products With Heading', '1', 'YES: If items are displayed on order form by radio list, phpCOIN will "group" the products by the first word in the product display, with that word also used as a group title.  This means you can group products by name, description or price\r\nNO: Do not "group" products');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'clients', 'B', 'ORDER_EDIT_CLIENT', 'Clients: Allow Editing Info On Re-order', '1', 'YES will allow an existing client to edit their contact info when placing a new order. NO will display their contact info without allowing editing');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'orders', 'B', 'ORDER_SHOW_IP', 'Order Form: Show User IP', '1', 'YES: Display the users IP address and a warning about being tracked when placing a new order\r\nNO: Do not display the users IP address and warning about being tracked');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'package', 'S', '_CURRENCY_PREFIX', 'Currency Prefix String (ex. $)', '$', 'The currency notation prefix, placed in front of currency amounts where formatted to display');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'package', 'S', '_CURRENCY_SUFFIX', 'Currency Suffix String (ex. USD)', '(USD)', 'The currency notation suffix, placed following currency amounts where formatted to display');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'package', 'S', '_DB_PKG_LANG', 'Language: Database Setting', 'lang_english', 'Which language files to load, and can  be overridden by config.php setting. The language file directory and files MUST exist prior to selection');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'package', 'S', '_DB_PKG_LOCALE', 'Package Locale: Datetime Display', 'en_EN', 'Some examples:\r\nBulgaria - bg_BG\r\nEnglish  - en_EN (or English)\r\nFrench   - fr_FR\r\nGerman  - German\r\nSwedish  - sv_SE\r\nSpanish  - Spanish');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_DB_PKG_THEME', 'Theme: Database Setting', 'earthtone', 'Which theme files to load, can be overridden by config.php setting. The theme file directory and files MUST exist prior to selection');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'I', '_NUMBER_FORMAT_ID', 'Number Format (see notes)', '3', 'Example number 1234.56\r\nValue = 1	Output: 1234\r\nValue = 2	Output: 1234.56\r\nValue = 3	Output: 1,234.56\r\nValue = 4	Output: 1 234,56\r\nValue = 5	Output: 1.234,56');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_PKG_NAME_SHORT', 'Site Name: Short', '%DOMAINNAME%', 'For display of site name (short version). This variable is used extensively in emails and various pages');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_PKG_NAME_LONG', 'Site Name: Long', '%DOMAINNAME% WebServices', 'Site long name for display, emails, etc. Basically longer version of the site short name');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'email', 'S', '_PKG_EMAIL_MAIL', 'Email: Default Email', 'support@%DOMAINNAME%', 'Last resort email address used when phpCOIN cannot locate a mail contacts entry');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_PKG_TOP_GREETING', 'Site Top Block Greeting Text', 'Welcome To %DOMAINNAME% WebServices', 'The Site Top Block (header) greeting text displayed if logo is not enabled.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'I', '_PKG_DATE_SERVER_OFFSET', 'Date Display: Server Offset In Hours', '0', 'Add or subtract this many hours from server time for the timestamps recorded / displayed');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_PKG_DATE_FORMAT_SHORT_DT', 'Date Display: Format For Short Date', 'Y-m-d', 'The display format for the short date, not locale setting dependent. See http://www.php.net/manual/en/function.date.php for formatting options');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_PKG_DATE_FORMAT_SHORT_DTTM', 'Date Display: Format For Short DateTime', 'Y-m-d H:i:s', 'The display format for the short datetime, not locale setting dependent. See http://www.php.net/manual/en/function.date.php for formatting options');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_PKG_DATE_FORMAT_HEADER', 'Date Display: Format For Header Row', '%A %B %d, %Y', 'The display format for the header block date display. See http://www.php.net/manual/en/function.strftime.php for formatting options');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'S', '_PKG_DATE_FORMAT_PRINT', 'Date Display: Format for Printing', '%A- %B %d, %Y @ %H:%M:%S %Z', 'The display format for the footer printed items date display. See http://www.php.net/manual/en/function.strftime.php for formatting options');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'admin', 'B', '_PKG_ENABLE_IP_BAN', 'Banned IP Addresses: Enable', '1', 'YES: Enables admin to create a list of banned IP addresses that can not view the site. Banned IPs are redirected to error message');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'email', 'B', '_PKG_ENABLE_EMAIL_ARCHIVE', 'Email Archive: Enable', '1', 'YES: Archive ALL emails sent by or through phpCOIN\r\nNO: Archive no emails sent by or through phpCOIN');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'operation', 'package', 'R', 'PAYLINK_EXCHANGE_RATE', 'Exchange Rate For PayLinks', '0', 'If you show your prices in one currency and collect payment in another, enter the exchange rate here. If you do not use different currencies, leave this as either 1 or 0');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'package', 'I', 'USE_LOGIN_MENUBOX', 'Use Login Menu Box', '1', 'LEFT COLUMN: A login/logoff box will be displayed in the left column before any menus, and the logon/logoff button in the header will be disabled.\r\nRIGHT COLUMN: A login/logoff box will be displayed in the right column before any menus, and the logon/logoff button in the header will be disabled\r\nNONE: phpCOIN defaults to normal operation\r\nMake sure that Admin -> Parameters -> Themes -> Disable Menu Columns (Left / Right) is also set to "NO" if left or right column option is chosen');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'pages', 'I', 'IPP_PAGES', 'List: Items Per Page', '15', 'The number of pages that are displayed in listing form on a page');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'summary', 'I', 'CC_DOMAIN_EXP_IN_DAYS', 'Domains Expiring: Days Before Expiry', '30', 'When Domains Expiring Summary is enabled, list domain names that will expire within this many days from today');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'summary', 'B', 'CC_DOMAIN_EXP_LIST_ENABLE', 'Domains Expiring: Enable', '1', 'YES: Display domain names that will expire within X days from today');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'summary', 'B', 'CC_DOMAIN_EXP_LIST_INCL_EXPRD', 'Domains Expiring: Include Expired', '1', 'YES: Include expired domains in Domains Expiring Summary');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'summary', 'I', 'CC_SACC_EXP_IN_DAYS', 'Server Accounts Expiring: Days Before Expiry', '30', 'When Server Accounts Expiring Summary is enabled, list domain names that will expire within this many days from today');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'summary', 'B', 'CC_SACC_EXP_LIST_ENABLE', 'Server Accounts Expiring: Enable', '1', 'YES: Display a summary of server accounts expiring within x days, or expired');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'summary', 'B', 'CC_SACC_EXP_LIST_INCL_EXPRD', 'Server Accounts Expiring: Include Expired', '1', 'YES: Include expired domains in Server Accounts Expiring Summary.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'summary', 'B', 'CC_SERVER_LIST_ENABLE', 'Servers Summary: Enable', '1', 'Display a list of all your servers, and the number of domains on each server');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'summary', 'B', 'PRODUCT_SUMMARY_ENABLE', 'Active Product Orders: Enable', '1', 'YES: Display a summary of all products that are on an active order.  If "Orders Listing Show Prod. Description" in the "orders" section is set to "Yes", then the description will show on the display, otherwise the product name will be shown.\r\nNO: Do not display');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'summary', 'B', 'SUMMARY_INVOICES_BY_TYPE', 'Invoices By Billing Cycle: Enable', '1', 'YES: Invoices will be listed in columnar format, with the amounts broken out by status and billing cycle\r\nNO: Lump all billing cycles into one column.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'summary', 'B', 'SUMMARY_INVOICES_BY_PRODUCT', 'Invoiced Products: Enable', '1', 'YES: Display a table with the number of times each product appears on an invoice, as well as the total value');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'summary', 'S', 'SUMMARY_INVOICES_BY_PRODUCT_IGNORE', 'Invoiced Products: Ignore these items', 'NOTE', 'A pipe-separated list of item names on invoices that should be ignored when building the "Invoiced Products Summary"');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'whois', 'R', 'DOMAIN_SETUP_FEE', 'WHOIS: Add Domain Setup Fee', '0', 'Any amount here other than zero will be added to the price of the product ordered as a domain setup fee, and the words " (plus setup fee)" will be appended to the item description on the order IF the whois module "Order" link was clicked.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'whois', 'S', 'WHOIS_AFFILIATE_LINK', 'WHOIS: Registrar Affiliate Link', 'http://www.123-reg.co.uk/affiliate.cgi?id=AF8763', 'If you are an affiliate of a domain registrar and wish to use an affiliate link to register domains, enter the URL of the link here.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'whois', 'B', 'WHOIS_EXT_LIST', 'WHOIS: Show Extensions As DropDown', '1', 'YES: Show allowable domain extensions as a drop-down list\r\nNO: Show the list as radio buttons');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'whois', 'B', 'WHOIS_DETAILS_NEW', 'Show Domain Details In New Window', '1', 'YES: Show details of registered domain in new "popup" window\r\nNO: Use current window');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'whois', 'B', 'WHOIS_INSTRUCTIONS_SHORT', 'WHOIS: Show Short Instructions', '0', 'YES: Show abbreviated domain selection instructions to surfers\r\nNO: Show the long instructions');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'whois', 'B', 'WHOIS_ENABLED', 'WHOIS: Enable', '1', 'YES: Module is enabled and ready for use\r\nNO: Module is deactivated, all menu buttons/links are automatically hidden, and no output from this module will be visible');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'ordering', 'whois', 'I', 'WHOIS_LINK', 'WHOIS: Domain Registration Operation', '1', '0= Do NOT add an "Order" or "Register" link\r\n1= Add "Order" link and go to "orders" page, passing in domain name and setting "New Domain" to "Yes"\r\n2= Add "Register" link, going to your affiliate link to register the domain. This option will open a new browser window to the registrar, and the original browser window will remain on this page');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'theme', 'buttons', 'B', '_HDR_MENU_BTTN_01', 'Header Menu Button 01: Enable', '1', 'Determines whether the button appears in the top menu.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'theme', 'buttons', 'B', '_HDR_MENU_BTTN_02', 'Header Menu Button 02: Enable', '1', 'Determines whether the button appears in the top menu.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'theme', 'buttons', 'B', '_HDR_MENU_BTTN_03', 'Header Menu Button 03: Enable', '1', 'Determines whether the button appears in the top menu.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'theme', 'buttons', 'B', '_HDR_MENU_BTTN_04', 'Header Menu Button 04: Enable', '1', 'Determines whether the button appears in the top menu.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'theme', 'buttons', 'B', '_HDR_MENU_BTTN_05', 'Header Menu Button 05: Enable', '1', 'Determines whether the button appears in the top menu.');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'theme', 'buttons', 'B', '_HDR_MENU_BTTN_06', 'Header Menu Button 06: Enable', '1', 'Determines whether the button appears in the top menu.');
