INSERT INTO `%PREFIX%reminders` (`overdue_id`, `overdue_title`, `overdue_active`, `overdue_days`, `overdue_subject`, `overdue_template`, `overdue_cc_support`) VALUES(5, 'Soon Due', '1', 0, 'Account Soon Due', 'Hello $_MTP[to_name];\r\n\r\nThis email is an automated reminder that your account recently had an invoice issued that will soon be due, with an outstanding account balance of $_MTP[cl_balance].  You may check the status of your account by clicking on the following link (requires login):\r\n$_MTP[invc_url]\r\n\r\n.  If you have already made payment arrangements, we thank you for your diligence and apologize for this automated reminder.\r\n\r\nThank you for continuing to choose $_MTP[Company_Name] for your web service needs.\r\n\r\nRespectfully Yours,\r\nBilling Department\r\n$_MTP[Company_Name]', 1);
INSERT INTO `%PREFIX%reminders` (`overdue_id`, `overdue_title`, `overdue_active`, `overdue_days`, `overdue_subject`, `overdue_template`, `overdue_cc_support`) VALUES(1, '10 Days Overdue', '1', 10, 'Account Overdue', 'Hello $_MTP[to_name];\r\n\r\nWe realize that everyone gets busy and occasionally overlooks things.  Unfortunately, we appear to have been overlooked this time.\r\n\r\nThis email is an automated notification that your account is now ten days overdue, with an outstanding balance of $_MTP[cl_balance].  You may check the status of your account by clicking on the following link (requires login):\r\n$_MTP[invc_url]\r\n\r\nWe ask that you take action to bring your account up-to-date, as specified in our <a href="$_MTP[_PKG_URL_BASE]/mod.php?mod=siteinfo&id=7" target="_new">Terms Of Service</a> page.  If you have already made payment arrangements, we thank you for your diligence and apologize for this automated reminder.\r\n\r\nThank you for continuing to choose $_MTP[Company_Name] for your web service needs.\r\n\r\nRespectfully Yours,\r\nBilling Department\r\n$_MTP[Company_Name]', 1);
INSERT INTO `%PREFIX%reminders` (`overdue_id`, `overdue_title`, `overdue_active`, `overdue_days`, `overdue_subject`, `overdue_template`, `overdue_cc_support`) VALUES(2, '20 Days Overdue', '1', 20, 'Account Seriously Overdue', 'Hello $_MTP[to_name];\r\n\r\nThis email is an automated notification that your account is now twenty days overdue, with an outstanding balance of $_MTP[cl_balance].  You may check the status of your account by clicking on the following link (requires login):\r\n$_MTP[invc_url]\r\n\r\nWe ask that you take immediate action to bring your account up-to-date, as specified in our <a href="$_MTP[_PKG_URL_BASE]/mod.php?mod=siteinfo&id=7" target="_new">Terms Of Service</a> page.  Failure to do so may result in additional collection steps and/or costs.  If you have already made payment arrangements, we thank you and apologize for this automated reminder.\r\n\r\nThank you for continuing to choose $_MTP[Company_Name] for your web service needs.\r\n\r\nRespectfully Yours,\r\nCollection Department\r\n$_MTP[Company_Name]', 1);
INSERT INTO `%PREFIX%reminders` (`overdue_id`, `overdue_title`, `overdue_active`, `overdue_days`, `overdue_subject`, `overdue_template`, `overdue_cc_support`) VALUES(3, '30 Days Overdue', '1', 30, 'Account Suspended', 'Hello $_MTP[to_name];\r\n\r\nThis email is an automated notification that your account is now thirty days overdue, with an outstanding balance of $_MTP[cl_balance].  You may check the status of your account by clicking on the following link (requires login):\r\n$_MTP[invc_url]\r\n\r\nThis is a very serious situation, and your account has been suspended.  To have your account re-instated, you must pay your outstanding balance in full plus a re-instatement fee of $20, as specified in our <a href="$_MTP[_PKG_URL_BASE]/mod.php?mod=siteinfo&id=7" target="_new">Terms Of Service</a> page.  Failure to pay everything owed within the next 15 days WILL result in ALL of your website, database and email files being deleted FOREVER.\r\n\r\nIf you have already made payment arrangements, we thank you and apologize for this automated reminder.\r\n\r\n\r\nRespectfully Yours,\r\nCollection Manager\r\n$_MTP[Company_Name]', 1);
INSERT INTO `%PREFIX%reminders` (`overdue_id`, `overdue_title`, `overdue_active`, `overdue_days`, `overdue_subject`, `overdue_template`, `overdue_cc_support`) VALUES(4, '1 Day Overdue', '1', 1, 'A Gentle Reminder', 'Hello $_MTP[to_name];\r\n\r\nWe realize that everyone gets busy and occasionally overlooks things.  Unfortunately, we appear to have been overlooked this time.\r\n\r\nThis email is an automated notification that your account is now overdue, with an outstanding balance of $_MTP[cl_balance].  You may check the status of your account by clicking on the following link (requires login):\r\n$_MTP[invc_url]\r\n\r\nWe ask that you take action to bring your account up-to-date, as specified in our <a href="$_MTP[_PKG_URL_BASE]/mod.php?mod=siteinfo&id=7" target="_new">Terms Of Service</a> page.  If you have already made payment arrangements, we thank you for your diligence and apologize for this automated reminder.\r\n\r\nThank you for continuing to choose $_MTP[Company_Name] for your web service needs.\r\n\r\nRespectfully Yours,\r\nBilling Department\r\n$_MTP[Company_Name]', 1);
