or_flag` tinyint(1) NOT NULL default '0', `os_cor_type` varchar(32) NOT NULL default '', `os_cor_opt_bill_cycle` varchar(32) NOT NULL default '', `os_cor_opt_payment` varchar(32) NOT NULL default '', `os_cor_disk` smallint(6) NOT NULL default '0', `os_cor_disk_units` char(3) NOT NULL default 'Mb', `os_cor_traffic` smallint(6) NOT NULL default '0', `os_cor_traffic_units` char(3) NOT NULL default 'Gb', `os_cor_dbs` smallint(6) NOT NULL default '0', `os_cor_mailboxes` smallint(6) NOT NULL default '0', `os_cor_unique_ip` tinyint(1) NOT NULL default '0', `os_cor_shop_cart` tinyint(1) NOT NULL default '0', `os_cor_sec_cert` tinyint(1) NOT NULL default '0', `os_cor_site_pages` tinyint(4) NOT NULL default '0', `os_cor_comments` text NOT NULL, `os_cor_optfld_01` varchar(50) NOT NULL default '', `os_cor_optfld_02` varchar(50) NOT NULL default '', `os_cor_optfld_03` varchar(50) NOT NULL default '', `os_cor_optfld_04` varchar(50) NOT NULL default '', `os_cor_optfld_05` varchar(50) NOT NULL default '', PRIMARY KEY  (`os_s_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Site Orders Module Sessions Table';
