CREATE TABLE `%PREFIX%parameters` (`parm_id` int(11) NOT NULL auto_increment, `parm_group` varchar(10) NOT NULL default 'common', `parm_group_sub` varchar(10) NOT NULL default 'undefined', `parm_type` char(1) NOT NULL default 'S', `parm_name` varchar(50) NOT NULL default '', `parm_desc` varchar(50) NOT NULL default '', `parm_value` varchar(100) NOT NULL default '', `parm_notes` text NOT NULL, PRIMARY KEY  (`parm_id`), UNIQUE KEY `parm_name` (`parm_name`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Site Parameters Table';
DROP TABLE IF EXISTS `%PREFIX%products`;
CREATE TABLE `%PREFIX%products` (`prod_id` smallint(6) NOT NULL auto_increment, `prod_status` tinyint(1) NOT NULL default '1', `prod_name` varchar(20) NOT NULL default '', `prod_desc` text NOT NULL default '', `prod_unit_cost` double NOT NULL default '0', `prod_client_scope` int(11) NOT NULL default '0', `prod_apply_tax_01` tinyint(1) NOT NULL default '1', `prod_apply_tax_02` tinyint(1) NOT NULL default '1', `prod_calc_tax_02_pb` tinyint(1) NOT NULL default '0', `prod_dom_type` tinyint(2) NOT NULL default '0', `prod_allow_domains` smallint(6) NOT NULL default '0', `prod_allow_subdomains` smallint(6) NOT NULL default '0', `prod_allow_disk_space_mb` int(11) NOT NULL default '0', `prod_allow_traffic_mb` int(11) NOT NULL default '0', `prod_allow_mailboxes` smallint(6) NOT NULL default '0', `prod_allow_databases` smallint(6) NOT NULL default '0', `prod_cg_01` tinyint(1) NOT NULL default '0', `prod_cg_02` tinyint(1) NOT NULL default '0', `prod_cg_03` tinyint(1) NOT NULL default '0', `prod_cg_04` tinyint(1) NOT NULL default '0', `prod_cg_05` tinyint(1) NOT NULL default '0', `prod_cg_06` tinyint(1) NOT NULL default '0', `prod_cg_07` tinyint(1) NOT NULL default '0', `prod_cg_08` tinyint(1) NOT NULL default '0', PRIMARY KEY  (`prod_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Site Products Table';
DROP TABLE IF EXISTS `%PREFIX%reminders`;
CREATE TABLE `%PREFIX%reminders` (`overdue_id` int(11) NOT NULL auto_increment, `overdue_title` varchar(50) NOT NULL default '', `overdue_active` char(1) NOT NULL default '1', `overdue_days` tinyint(4) NOT NULL default '10', `overdue_subject` varchar(150) NOT NULL default '', `overdue_template` text NOT NULL, `overdue_cc_support` tinyint(1) NOT NULL default '0', UNIQUE KEY `overdue_id` (`overdue_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `%PREFIX%server_info`;
CREATE TABLE `%PREFIX%server_info` (`si_id` smallint(6) NOT NULL auto_increment, `si_name` varchar(20) NOT NULL default '', `si_ip` varchar(15) NOT NULL default '000.000.000.000', `si_ns_01` varchar(50) NOT NULL default '', `si_ns_01_ip` varchar(15) NOT NULL default '000.000.000.000', `si_ns_02` varchar(50) NOT NULL default '', `si_ns_02_ip` varchar(15) NOT NULL default '000.000.000.000', `si_cp_url` varchar(50) NOT NULL default '', `si_cp_url_port` int(11) NOT NULL default '0', PRIMARY KEY  (`si_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Site Server Info Table';
DROP TABLE IF EXISTS `%PREFIX%sessions`;
CREATE TABLE `%PREFIX%sessions` (`s_id` varchar(36) NOT NULL default '', `s_time_init` int(11) NOT NULL default '0', `s_time_last` int(11) NOT NULL default '0', `s_ip` varchar(16) NOT NULL default '000.000.000.000', `s_is_admin` tinyint(1) NOT NULL default '0', `s_is_user` tinyint(1) NOT NULL default '0', `s_time_last_contact` int(11) NOT NULL default '0', `s_time_last_order` int(11) NOT NULL default '0', PRIMARY KEY  (`s_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Site Sessions Table';
DROP TABLE IF EXISTS `%PREFIX%site_info`;
CREATE TABLE `%PREFIX%site_info` (`si_id` int(11) NOT NULL auto_increment, `si_group` varchar(32) NOT NULL, `si_name` varchar(32) NOT NULL, `si_seq_no` int(11) NOT NULL, `si_desc` varchar(50) NOT NULL, `si_block_it` tinyint(1) NOT NULL default '0', `si_title` varchar(50) NOT NULL, `si_code` longtext, `si_status` tinyint(1) NOT NULL default '0', `si_footer_menu` tinyint(1) NOT NULL default '1', PRIMARY KEY  (`si_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Site SiteInfo Table';
DROP TABLE IF EXISTS `%PREFIX%suppliers`;
CREATE TABLE `%PREFIX%suppliers` (`s_id` int(11) NOT NULL default '0', `s_status` varchar(20) NOT NULL default 'pending', `s_company` varchar(50) NOT NULL default '', `s_name_first` varchar(20) NOT NULL default '', `s_name_last` varchar(20) NOT NULL default '', `s_addr_01` varchar(50) NOT NULL default '', `s_addr_02` varchar(50) NOT NULL default '', `s_city` varchar(50) NOT NULL default '', `s_state_prov` varchar(50) NOT NULL default '', `s_country` varchar(50) NOT NULL default '', `s_zip_code` varchar(12) NOT NULL default '', `s_phone` varchar(20) NOT NULL default '', `s_fax` varchar(20) NOT NULL default '', `s_tollfree` varchar(20) NOT NULL default '', `s_email` varchar(50) NOT NULL default '', `s_taxid` varchar(20) NOT NULL default '', `s_account` varchar(20) NOT NULL default '', `s_terms` varchar(20) NOT NULL, `s_notes` text NOT NULL, PRIMARY KEY  (`s_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Site Suppliers Module Table';
DROP TABLE IF EXISTS `%PREFIX%suppliers_contacts`;
CREATE TABLE `%PREFIX%suppliers_contacts` (`contacts_id` int(11) NOT NULL auto_increment, `contacts_s_id` int(11) NOT NULL default '0', `contacts_name_first` varchar(20) NOT NULL default '', `contacts_name_last` varchar(20) NOT NULL default '', `contacts_email` varchar(50) NOT NULL default '', UNIQUE KEY `contacts_id` (`contacts_id`), KEY `contacts_s_id` (`contacts_s_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Site Suppliers Additional Emails Table';
DROP TABLE IF EXISTS `%PREFIX%todo`;
CREATE TABLE `%PREFIX%todo` (`todo_id` smallint(6) NOT NULL auto_increment, `todo_admin_id` smallint(6) default '0', `todo_title` varchar(255) default '0', `todo_text` text, `todo_duedate` varchar(15) default '0', `todo_entered` varchar(15) default '0', `todo_completed` tinyint(1) default '0', `todo_priority` int(2) default '1', PRIMARY KEY  (`todo_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `%PREFIX%topics`;
CREATE TABLE `%PREFIX%topics` (`topic_id` mediumint(9) NOT NULL auto_increment, `topic_pos` smallint(6) NOT NULL default '0', `topic_name` varchar(50) NOT NULL default '', `topic_desc` varchar(50) NOT NULL default '', `topic_icon` smallint(6) NOT NULL default '1', PRIMARY KEY  (`topic_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Site Topics Table';
DROP TABLE IF EXISTS `%PREFIX%vendors`;
CREATE TABLE `%PREFIX%vendors` (`vendor_id` smallint(6) NOT NULL auto_increment, `vendor_status` tinyint(1) NOT NULL default '0', `vendor_name` varchar(20) NOT NULL default '', `vendor_buy_parm` varchar(30) NOT NULL default 'buy', `vendor_buy_parm_val` varchar(10) NOT NULL default '1', `vendor_url` varchar(50) NOT NULL default '', `vendor_use_ipn` tinyint(1) NOT NULL default '0', `vendor_notes` text NOT NULL, PRIMARY KEY  (`vendor_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Site Vendors Table';
DROP TABLE IF EXISTS `%PREFIX%vendors_prods`;
CREATE TABLE `%PREFIX%vendors_prods` (`vprod_id` smallint(6) NOT NULL auto_increment, `vprod_vendor_id` smallint(6) NOT NULL default '0', `vprod_prod_id` smallint(6) NOT NULL default '0', `vprod_desc` varchar(50) NOT NULL default '', `vprod_order_link` text NOT NULL, PRIMARY KEY  (`vprod_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Site Vendor Products Table';
DROP TABLE IF EXISTS `%PREFIX%whois`;
CREATE TABLE `%PREFIX%whois` (`whois_id` int(11) NOT NULL auto_increment, `whois_server` varchar(100) NOT NULL default '', `whois_nomatch` varchar(40) NOT NULL default '', `whois_value` varchar(20) NOT NULL default '', `whois_display` varchar(25) NOT NULL default '', `whois_include` tinyint(1) NOT NULL default '1', `whois_prod_id` int(11) NOT NULL default '0', `whois_notes` text, UNIQUE KEY `whois_id` (`whois_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `%PREFIX%versions`;
CREATE TABLE `%PREFIX%versions` (`v_id` smallint(6) NOT NULL auto_increment, `v_ts` varchar(10) NOT NULL default '', `v_ver` varchar(8) NOT NULL default '', `v_type` varchar(10) NOT NULL default 'Install', PRIMARY KEY  (`v_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Package Versions Table';
