INSERT INTO %PREFIX%parameters (`parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES ('automation', 'invoices', 'B', 'INVC_SOON_REMINDERS_ENABLE', 'Invoice Due- Autosend soon-due reminders', '1', 'If "Yes", phpCOIN will auto-send nag email message zero half way between teh invoice issued and due dates. If "No", there will be NO reminders that an invoice will soon be due.');
INSERT INTO %PREFIX%parameters (`parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES ('automation', 'invoices', 'B', 'INVC_ACOPY_NEW_TERMS', 'Invoice AutoCopy Terms- Insert New', '0', 'If "Yes", phpCOIN will replace the existing terms on auto-copied invoices with whatever the current terms are in lang_config.php. If "No", phpCOIN will copy any existing terms on the invoice as-is');
INSERT INTO %PREFIX%reminders (`overdue_id`, `overdue_title`, `overdue_active`, `overdue_days`, `overdue_subject`, `overdue_template`, `overdue_cc_support`) VALUES (0, 'Soon Due', '1', 0, 'Account Soon Due', 'Hello $_MTP[to_name];\r\n\r\nThis email is an automated reminder that your account recently had an invoice issued that will soon be due, with an outstanding account balance of $_MTP[cl_balance].  You may check the status of your account by clicking on the following link (requires login):\r\n$_MTP[invc_url]\r\n\r\n.  If you have already made payment arrangements, we thank you for your diligence and apologize for this automated reminder.\r\n\r\nThank you for continuing to choose $_MTP[Company_Name] for your web service needs.\r\n\r\nRespectfully Yours,\r\nBilling Department\r\n$_MTP[Company_Name]', 1);
