INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'summary', 'B', 'SHOW_EMPTY_BILL_STATUS', 'Bills By Billing Cycle: Show Unused Status', '0', 'YES: The bill columnar listing will show a row for each bill status, whether any bills have that status or not.\r\nNO: Only statuses that are used on a bill will be shown');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'layout', 'summary', 'B', 'SHOW_BILL_ZERO_TOTAL', 'Bills By Billing Cycle: $0.00 Totals', '1', 'YES: Zero value billing cycle totals will display as $0.00\r\nNO: Zero value billing cycle totals will display as a blank space');
INSERT INTO `%PREFIX%parameters` (`parm_id`, `parm_group`, `parm_group_sub`, `parm_type`, `parm_name`, `parm_desc`, `parm_value`, `parm_notes`) VALUES('', 'enable', 'summary', 'B', 'SUMMARY_BILLS_BY_TYPE', 'Bills By Billing Cycle: Enable', '1', 'YES: Bills will be listed in columnar format, with the amounts broken out by status and billing cycle\r\nNO: Lump all billing cycles into one column.');
